    private void lblBotaoNovoJogoMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblBotaoNovoJogoMouseReleased

        lblBotaoNovoJogo.setBounds(330, 210, 380, 80);
        // pede o nome do jogador
        String nome = JOptionPane.showInputDialog(null, "Digite seu nome:", "Novo Jogador", JOptionPane.QUESTION_MESSAGE);
        
        if(nome != null){ // se o jogador nao clicou em cancelar
            while (nome.equals("") || nome.length() >= 15) { // enquanto o usuario nao digitar um nome valido
                if (nome.length() >= 15) {
                    JOptionPane.showMessageDialog(null, "O nome deve conter no maximo 15 letras!");
                } else if (nome.equals("")) {
                    JOptionPane.showMessageDialog(null, "Por favor insira um nome!");
                }
                nome = JOptionPane.showInputDialog("Digite seu nome:");
            }
            // atualiza os dados do jogador e vai para a criação dos personagens
            jogador.setNome(nome);
            setVisible(false);
            new CriarJogador().setVisible(true);
        }
        
        
        
    }//GEN-LAST:event_lblBotaoNovoJogoMouseReleased

