    private void lblBotaoContinuarMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblBotaoContinuarMouseReleased
        // TODO add your handling code here:
        lblBotaoContinuar.setBounds(330, 300, 380, 80);
        
                
        retorno = manipulaArquivos.carregaJogo();
        
        if(retorno.getNome() == null){
            JOptionPane.showMessageDialog(null,"Não foi encontrado nenhum jogo.","Erro ao localizar jogo",JOptionPane.ERROR_MESSAGE);
        }else{
            jogador = retorno;
            setVisible(false);
            new Combate().setVisible(true);
        }

    }//GEN-LAST:event_lblBotaoContinuarMouseReleased

